Create internal/executor/gatherer.go:

```go
type contextGatherer struct {
    store      storage.Storage
    sandboxMgr sandbox.Manager
}

func NewContextGatherer(store storage.Storage, sandboxMgr sandbox.Manager) ContextGatherer

func (g *contextGatherer) GatherContext(ctx context.Context, issue *types.Issue, sb *sandbox.Sandbox) (*PromptContext, error) {
    pc := &PromptContext{Issue: issue}

    // 1. Get parent mission if this is a child task
    pc.ParentMission, _ = g.GetParentMission(ctx, issue)

    // 2. Get related issues (blockers, dependents, siblings)
    pc.RelatedIssues, _ = g.GetRelatedIssues(ctx, issue)

    // 3. Get previous execution attempts
    pc.PreviousAttempts, _ = g.GetPreviousAttempts(ctx, issue.ID)

    // 4. Get quality gate status if any
    pc.QualityGateStatus = g.getQualityGateStatus(ctx, issue)

    // 5. Get sandbox context if available
    if sb != nil {
        pc.Sandbox, _ = g.sandboxMgr.InspectState(ctx, sb)
        pc.GitState = g.getGitState(ctx, sb)
    }

    // 6. Analyze resume state
    if len(pc.PreviousAttempts) > 0 && sb != nil {
        pc.ResumeHint, _ = g.AnalyzeResumeState(ctx, sb, pc.PreviousAttempts)
    }

    return pc, nil
}

func (g *contextGatherer) GetParentMission(ctx context.Context, issue *types.Issue) (*types.Issue, error) {
    // Find parent via parent-child dependency
    deps, err := g.store.GetDependencies(ctx, issue.ID)
    for _, dep := range deps {
        // Check if this is a parent relationship
        depRecords, _ := g.store.GetDependencyRecords(ctx, issue.ID)
        for _, record := range depRecords {
            if record.DependsOnID == dep.ID && record.Type == types.DepParentChild {
                return dep, nil
            }
        }
    }
    return nil, nil
}

func (g *contextGatherer) GetRelatedIssues(ctx context.Context, issue *types.Issue) (*RelatedIssues, error) {
    ri := &RelatedIssues{}

    // Get blockers
    ri.Blockers, _ = g.store.GetDependencies(ctx, issue.ID)

    // Get dependents
    ri.Dependents, _ = g.store.GetDependents(ctx, issue.ID)

    // Get siblings (other children of same parent)
    if parent, _ := g.GetParentMission(ctx, issue); parent != nil {
        allChildren, _ := g.store.GetDependents(ctx, parent.ID)
        for _, child := range allChildren {
            if child.ID != issue.ID {
                ri.Siblings = append(ri.Siblings, child)
            }
        }
    }

    return ri, nil
}

func (g *contextGatherer) AnalyzeResumeState(ctx context.Context, sb *sandbox.Sandbox, attempts []*ExecutionAttempt) (string, error) {
    // Analyze what was done and what remains
    lastAttempt := attempts[len(attempts)-1]

    var hint strings.Builder
    hint.WriteString(fmt.Sprintf("Previous attempt #%d ", lastAttempt.AttemptNumber))
    if lastAttempt.Success {
        hint.WriteString("succeeded but may have punted work. ")
    } else {
        hint.WriteString(fmt.Sprintf("failed with exit code %d. ", lastAttempt.ExitCode))
    }

    // Add git state
    if sbCtx, _ := g.sandboxMgr.InspectState(ctx, sb); sbCtx != nil {
        if len(sbCtx.ModifiedFiles) > 0 {
            hint.WriteString(fmt.Sprintf("Modified files: %d. ", len(sbCtx.ModifiedFiles)))
        }
        if sbCtx.GitStatus != "" {
            hint.WriteString("Uncommitted changes present. ")
        }
    }

    hint.WriteString("Please assess the current state and continue from where we left off.")
    return hint.String(), nil
}
```

