type PromptBuilder struct {
    template *template.Template
}

func NewPromptBuilder() *PromptBuilder

func (pb *PromptBuilder) BuildPrompt(ctx *PromptContext) string {
    // Use text/template to build structured prompt
    var buf bytes.Buffer
    pb.template.Execute(&buf, ctx)
    return buf.String()
}
```

